mapper.update:
update 

set
	DR = 1 
where 
	SUPPLIER_ID = #{SUPPLIER_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}

update base_supplier
set DEF1 =#{amount}
 where 
	SUPPLIER_ID = #{SUPPLIER_ID}
 and PK_SOBOOKS = #{PK_SOBOOKS}
 and IFNULL(DR,0)=0

mapper.select:
select DEF1
from base_supplier
 where 
SUPPLIER_ID = #{SUPPLIER_ID}
 and PK_SOBOOKS = #{PK_SOBOOKS}
 and IFNULL(DR,0)=0

select 

from 

where 
	SUPPLIER_ID = #{SUPPLIER_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(DR,0)=0

select 

from 

where 
	SUPPLIERCODE = #{SUPPLIERCODE}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(DR,0)=0

select a.SUPPLIERCODE, a.SUPPLIERNAME, a.SIMPLENAME, a.PHONE, a.COMPANY, a.ADDRESS, a.YICODE, a.TELEPHONE, a.CREATETIME, a.PAGING, a.FAX, a.LINKMAN, a.DISTRIBUTIONMODE, a.CREDITDEGREE, a.NOTE, a.OPENBANK, a.BANKACCOUNT, a.MAILCODE, a.EMAIL, a.NETADDR, a.USER_ID, a.SUPPLIER_ID from a where 1=1
and ( a.SUPPLIERNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.PHONE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.COMPANY LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.ADDRESS LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.DISTRIBUTIONMODE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.NOTE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.USER_ID LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and a.CREATETIME >= #{pd.lastStart}
and a.CREATETIME <= #{pd.lastEnd}
and PK_SOBOOKS = #{pd.PK_SOBOOKS} and IFNULL(DR,0)=0 order by a.CREATETIME desc
