mapper.update:
update

set DR = 1
where 
	INORDER_ID = #{INORDER_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}

update

set 
	SUPPSETBILL_ID = #{SUPPSETBILL_ID},
	ISSETTLEMENTED = #{ISSETTLEMENTED}
where 
	INORDER_ID = #{INORDER_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0

mapper.select:
select 
	
from
 
where 
	 INORDER_ID = #{INORDER_ID}
    and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0
	
		and BILLTYPE = #{BILLTYPE}

select 
	
from
 
where 
	 SUPPSETBILL_ID = #{SUPPSETBILL_ID}
    and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0
	
		and BILLTYPE = #{BILLTYPE}

select a.PK_SOBOOKS, a.INORDER_ID, a.LDATE, a.BILLCODE, a.SUPPLIER_ID, a.SUPPLIERNO, a.USER_ID, a.WAREHOUSE_ID, a.ALLAMOUNT, a.BILLTYPE, a.BILLSTATUS, a.PAIDAMOUNT, a.UNPAIDAMOUNT, a.THISPAY, a.SUBBRANCH, a.NOTE, a.ISSETTLEMENTED, b.SUPPLIERNAME SUPPLIERNAME from a left join base_supplier b on a.SUPPLIER_ID = b.SUPPLIER_ID where 1=1 and a.PK_SOBOOKS = #{pd.PK_SOBOOKS} and IFNULL(a.dr,0)=0
and ( a.NOTE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)
and a.SUPPLIER_ID= #{pd.SUPPLIER_ID}
and a.ISSETTLEMENTED = #{pd.ISSETTLEMENTED}
and a.BILLSTATUS = #{pd.BILLSTATUS}
and a.BILLTYPE = #{pd.BILLTYPE}
order by a.LASTTIME DESC

select a.INORDER_ID, a.BILLCODE, b.SUPPLIERNAME SUPPLIERNAME, a.SUPPLIERNO, a.ALLAMOUNT, a.UNPAIDAMOUNT, a.PAIDAMOUNT, a.THISPAY, a.BILLTYPE, a.ISSETTLEMENTED, a.USER_ID, a.NOTE from a left join base_supplier b on a.SUPPLIER_ID = b.SUPPLIER_ID where 1=1 and a.PK_SOBOOKS = #{pd.PK_SOBOOKS} and IFNULL(a.dr,0)=0
and ( NOTE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)
and a.SUPPLIER_ID= #{pd.SUPPLIER_ID}
and a.ISSETTLEMENTED != 1
and a.ISSETTLEMENTED = #{pd.ISSETTLEMENTED}
and a.BILLSTATUS = 2

mapper.update:
update
set DR = 1 where INORDER_ID in
#{item}

