mapper.select:
select a.BILLCODE,d.GOODNAME,b.UNITPRICE_ID,b.PNUMBER,a.ALLAMOUNT,a.PAIDAMOUNT,a.LASTTIME,c.USERNAME,a.BILLTYPE, e.SUPPLIERNAME as startplace , f.WHNAME as endplace from psi_inorder as a left join psi_inorder_body as b on a.INORDER_ID=b.INORDER_ID left join sys_user as c on a.USER_ID=c.USER_ID left join base_good as d on b.GOODCODE_ID=d.GOODCODE left join base_supplier as e on a.SUPPLIER_ID=e.SUPPLIER_ID left join base_warehouse as f on b.WAREHOUSE_ID=f.WAREHOUSE_ID where a.PK_SOBOOKS=1 and ifnull(a.dr,0)=0
and d.GOODNAME like concat('%',#{pd.keywords},'%') or c.USERNAME like concat('%',#{pd.keywords},'%') or e.SUPPLIERNAME like concat('%',#{pd.keywords},'%') or f.WHNAME like concat('%',#{pd.keywords},'%')
and Date(a.LASTTIME) BETWEEN #{pd.lastStart} and #{pd.lastEnd}
and b.WAREHOUSE_ID=#{pd.WAREHOUSE_ID}
and a.BILLTYPE = #{pd.BILLTYPE}
order by a.LASTTIME desc

select a.INORDER_ID,a.BILLCODE , a.BILLTYPE,c.GOODNAME ,d.NAME , e.SUPPLIERNAME , f.WHNAME ,a.ALLAMOUNT,a.PAIDAMOUNT,a.UNPAIDAMOUNT, b.PNUMBER,a.CREATETIME,b.UNITPRICE_ID,g.PAYMETHOD,h.PAYMETHODNAME from psi_inorder as a left JOIN psi_inorder_body as b on a.INORDER_ID=b.INORDER_ID left join base_good as c on b.GOODCODE_ID=c.GOODCODE left join sys_user as d on a.USER_ID=d.USER_ID left join base_supplier as e on a.SUPPLIER_ID=e.SUPPLIER_ID left join base_warehouse as f on b.WAREHOUSE_ID=f.WAREHOUSE_ID left join psi_suppsetbill as g on a.SUPPSETBILL_ID=g.SUPPSETBILL_ID left join base_paymethod as h on h.PAYMETHOD_ID=g.PAYMETHOD where a.PK_SOBOOKS=#{pd.PK_SOBOOKS} and ifnull(a.dr,0)=0 and a.BILLSTATUS=2
and c.GOODNAME like concat('%',#{pd.keywords},'%') or e.SUPPLIERNAME like concat('%',#{pd.keywords},'%') or d.NAME like concat('%',#{pd.keywords},'%') or f.WHNAME like concat('%',#{pd.keywords},'%')
and a.INORDER_ID in
${item}
and h.PAYMETHOD_ID=#{pd.PAYMETHOD_ID}
and a.BILLTYPE = #{pd.BILLTYPE}
