mapper.update:
update 

set
	SETTEDNUMANDID = #{SETTEDNUMANDID},
	BILLSTATUS = #{BILLSTATUS}
where 
	SUPPSETBILL_ID = #{SUPPSETBILL_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(DR,0)=0

mapper.select:
select 

from 

where 
	SUPPSETBILL_ID = #{SUPPSETBILL_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(DR,0)=0

select a.SUPPSETBILL_ID, a.LDATE, a.BILLCODE, a.BILLTYPE, a.BILLSTATUS, a.USER_ID, a.FROMUNIT, s.SUPPLIERNAME as FROMUNITNAME, a.DISTRIBUTIONMODE, a.INVOICETYPE, a.BILLNO, p.PAYMETHODNAME as PAYMETHOD, a.PAYABLEAMOUNT, a.PAYMENTAMOUNT, a.PAYABLEALLAM, a.NOTE, a.PK_SOBOOKS from a, s, p where 1=1 and a.FROMUNIT = s.SUPPLIER_ID and a.PAYMETHOD = p.PAYMETHOD_ID
and ( s.SUPPLIERNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.BILLCODE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.PAYMETHOD LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.DISTRIBUTIONMODE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.NOTE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and a.CREATETIME >= #{pd.lastStart}
and a.CREATETIME <= #{pd.lastEnd}
and a.BILLSTATUS = #{pd.billstatus}
and a.PK_SOBOOKS = #{pd.PK_SOBOOKS} and IFNULL(a.DR,0)=0 order by a.LASTTIME desc

select  a.BILLCODE ,a.INORDER_IDS,b.PAYMETHODNAME, a.BILLNO,c.NAME
from psi_suppsetbill as a left join base_paymethod as b on a.PAYMETHOD=b.PAYMETHOD_ID
left join sys_user as c on a.USER_ID=c.USER_ID
where  a.PK_SOBOOKS = #{PK_SOBOOKS}
and IFNULL(a.DR,0)=0 
and a.BILLSTATUS=2

	and	date_format(a.LASTTIME,'%Y-%m-%d')=#{date}
	or date_format(a.CREATETIME,'%Y-%m-%d')=#{date}


	
		and a.CREATETIME BETWEEN #{lastStart} and  #{lastEnd}	
	



	and a.PAYMETHOD=#{PAYMETHOD_ID}


	and c.NAME like concat('%',#{keywords},'%')
