mapper.select:
select SUPPLIERCODE ,SUPPLIERNAME,COMPANY,PHONE,SUPPLIER_ID from base_supplier where PK_SOBOOKS=#{PK_SOBOOKS}
and SUPPLIERNAME like concat('%',#{keyword},'%') or COMPANY like concat('%',#{keyword},'%')
and IFNULL(DR,0)=0 limit #{pageNum},10

select count(*) from base_supplier where PK_SOBOOKS=#{PK_SOBOOKS}
and SUPPLIERNAME like concat('%',#{keyword},'%') or COMPANY like concat('%',#{keyword},'%')
and IFNULL(DR,0)=0
