mapper.select:
select a.PNUMBER,a.AMOUNT,a.CREATETIME,d.NAME as UNIT
from psi_salebillbody as a left join psi_salebill as b on a.SALEBILL_ID=b.SALEBILL_ID
left join base_good as c  on c.GOODCODE=a.GOODCODE_ID
left join cp_spunit as d on d.SPUNIT_ID=c.CUNIT_ID 
where a.GOODCODE_ID=#{GOODCODE}
 and a.PK_SOBOOKS=#{PK_SOBOOKS} 
and IFNULL(a.dr,0)=0  
ORDER BY a.CREATETIME desc limit 0,5;

select a.GOODCODE_ID, c.GOODPIC ,c.GOODNAME,sum(a.AMOUNT) as ALLAMOUNT ,sum( a.PNUMBER ) as PNUMBER, count(*) as NUM, sum(a.PNUMBER)*(a.UNITPRICE_ID-c.DEF1) as maoLi from psi_salebillbody as a left join psi_salebill as b on a.SALEBILL_ID=b.SALEBILL_ID left join base_good as c on a.GOODCODE_ID=c.GOODCODE left join base_goodtype as d on c.GOODTYPE_ID=d.GOODTYPE_ID where a.PK_SOBOOKS=#{PK_SOBOOKS} and b.BILLTYPE=#{BILLTYPE} and IFNULL(a.dr,0)=0
and date_format(a.LASTTIME,'%Y-%m')=#{yearMouth}
and date_format(a.LASTTIME,'%Y-%m-%d') BETWEEN #{startTime} and #{endTime}
and d.TYPENAME like concat('%',#{TYPENAME},'%')
GROUP BY a.GOODCODE_ID
ORDER BY ALLAMOUNT desc
ORDER BY saleNum desc
ORDER BY NUM desc
ORDER BY maoLi desc
limit #{pageNum},#{pageSize}

SELECT a.SALEBILL_ID, a.BILLCODE,b.NAME ,c.CUATOMERNAME,a.NOTE,date_format(a.CREATETIME,'%Y-%m-%d') as CREATETIME ,a.ALLAMOUNT
from 
psi_salebill as a left join sys_user as b on a.USER_ID=b.USER_ID
left join base_customer as c on a.CUSTOMER_ID=c.CUSTOMER_ID
where a.BILLTYPE='2'
and a.PK_SOBOOKS=#{PK_SOBOOKS}
and IFNULL(a.dr,0)=0 

	
		 and date_format(a.CREATETIME,'%Y-%m-%d') BETWEEN #{lastStart} and #{lastEnd}
	


	and date_format(a.CREATETIME,'%Y-%m')=#{date}

order by a.CREATETIME
limit #{pageNum},#{pageSize}

SELECT count(*)
from 
psi_salebill as a left join sys_user as b on a.USER_ID=b.USER_ID
left join base_customer as c on a.CUSTOMER_ID=c.CUSTOMER_ID
where a.BILLTYPE='2'
and a.PK_SOBOOKS=#{PK_SOBOOKS}
and IFNULL(a.dr,0)=0 

	
		 and date_format(a.CREATETIME,'%Y-%m-%d') BETWEEN #{lastStart} and #{lastEnd}
	


	and date_format(a.CREATETIME,'%Y-%m')=#{date}

order by a.CREATETIME

select CUSTOMERSETBILL_ID,UNPAIDAMOUNT,ALLAMOUNT,SALEBILL_ID,BILLTYPE
from psi_salebill
where  PK_SOBOOKS=#{PK_SOBOOKS}
and IFNULL(dr,0)=0 

	and date_format(CREATETIME,'%Y-%m')=#{date}

select sum((a.AMOUNT-b.DEF1*a.PNUMBER)) as pureamount,sum(a.PNUMBER) as pnumber
from psi_salebillbody as a left join base_good as b on a.GOODCODE_ID=b.GOODCODE
where SALEBILL_ID=#{Id}

select CUSTOMERSETBILL_ID,UNPAIDAMOUNT,ALLAMOUNT,SALEBILL_ID,BILLTYPE
from psi_salebill
where  PK_SOBOOKS=#{PK_SOBOOKS}
and IFNULL(dr,0)=0 

	and date_format(CREATETIME,'%Y-%m-%d')=#{date}

select a.GOODCODE_ID, c.GOODPIC ,c.GOODNAME,a.AMOUNT , a.PNUMBER ,a.PNUMBER*(a.UNITPRICE_ID-c.DEF1) as MONEY, d.CUATOMERNAME,d.TELEPHONE,d.CUSTOMERCODE from psi_salebillbody as a left join psi_salebill as b on a.SALEBILL_ID=b.SALEBILL_ID left join base_good as c on a.GOODCODE_ID=c.GOODCODE left join base_customer as d on d.CUSTOMER_ID = b.CUSTOMER_ID where a.PK_SOBOOKS=#{PK_SOBOOKS} and b.BILLTYPE=#{BILLTYPE} and IFNULL(a.dr,0)=0
and date_format(a.LASTTIME,'%Y-%m')=#{yearMouth}
and date_format(a.LASTTIME,'%Y-%m-%d') BETWEEN #{startTime} and #{endTime}
ORDER BY ALLAMOUNT desc
ORDER BY PNUMBER desc
ORDER BY MONEY desc

select count(*) from psi_salebillbody as a left join psi_salebill as b on a.SALEBILL_ID=b.SALEBILL_ID left join base_good as c on a.GOODCODE_ID=c.GOODCODE left join base_customer as d on d.CUSTOMER_ID = b.CUSTOMER_ID where a.PK_SOBOOKS=#{PK_SOBOOKS} and b.BILLTYPE=#{BILLTYPE} and IFNULL(a.dr,0)=0 and date_format(a.LASTTIME,'%Y-%m')='2019-08'
and date_format(a.LASTTIME,'%Y-%m')=#{yearMouth}
and date_format(a.LASTTIME,'%Y-%m-%d') BETWEEN #{startTime} and #{endTime}
ORDER BY ALLAMOUNT desc
ORDER BY PNUMBER desc
ORDER BY MONEY desc

select a.GOODCODE_ID, c.GOODPIC ,c.GOODNAME,a.AMOUNT , a.PNUMBER ,a.PNUMBER*(a.UNITPRICE_ID-c.DEF1) as MONEY, d.NAME,e.ENTERPRISENAME,d.USER_ID from psi_salebillbody as a left join psi_salebill as b on a.SALEBILL_ID=b.SALEBILL_ID left join base_good as c on a.GOODCODE_ID=c.GOODCODE left join sys_user as d on d.USER_ID= b.USER_ID left join base_sobooks as e on e.SOBOOKS_ID = d.PK_SOBOOKS where a.PK_SOBOOKS=#{PK_SOBOOKS} and b.BILLTYPE=#{BILLTYPE} and IFNULL(a.dr,0)=0 and date_format(a.LASTTIME,'%Y-%m')='2019-08'
and date_format(a.LASTTIME,'%Y-%m')=#{yearMouth}
and date_format(a.LASTTIME,'%Y-%m-%d') BETWEEN #{startTime} and #{endTime}
ORDER BY ALLAMOUNT desc
ORDER BY PNUMBER desc
ORDER BY MONEY desc

select count(*) from psi_salebillbody as a left join psi_salebill as b on a.SALEBILL_ID=b.SALEBILL_ID left join base_good as c on a.GOODCODE_ID=c.GOODCODE left join sys_user as d on d.USER_ID= b.USER_ID left join base_sobooks as e on e.SOBOOKS_ID = d.PK_SOBOOKS where a.PK_SOBOOKS=#{PK_SOBOOKS} and b.BILLTYPE=#{BILLTYPE} and IFNULL(a.dr,0)=0 and date_format(a.LASTTIME,'%Y-%m')='2019-08'
and date_format(a.LASTTIME,'%Y-%m')=#{yearMouth}
and date_format(a.LASTTIME,'%Y-%m-%d') BETWEEN #{startTime} and #{endTime}
ORDER BY ALLAMOUNT desc
ORDER BY PNUMBER desc
ORDER BY MONEY desc
