mapper.select:
select 

from 

where 
	SALEBILL_ID = #{SALEBILL_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(DR,0)=0

mapper.update:
update

set DR = 1
where 
	SALEBILL_ID = #{SALEBILL_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}

update

set SETTEDNUMANDID = #{SETTEDNUMANDID}
where 
	FGROUP_ID = #{FGROUP_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0

update

set 
	GOODCODE_ID = #{newGOODCODE}
where 
GOODCODE_ID = #{GOODCODE}
and PK_SOBOOKS = #{PK_SOBOOKS}

