mapper.update:
update

set DR = 1
where 
	SALEBILL_ID = #{SALEBILL_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}

update

set 
	CUSTOMERSETBILL_ID = #{CUSTOMERSETBILL_ID},
	ISSETTLEMENTED = #{ISSETTLEMENTED}
where 
	SALEBILL_ID = #{SALEBILL_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0

mapper.select:
select 
	
from
 
where 
	 SALEBILL_ID = #{SALEBILL_ID}
    and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0

select 
	
from
 
where 
	 CUSTOMERSETBILL_ID = #{CUSTOMERSETBILL_ID}
    and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0

select a.PK_SOBOOKS, a.SALEBILL_ID, a.LDATE, a.BILLCODE, a.CUSTOMER_ID, a.USER_ID, a.WAREHOUSE_ID, a.ALLAMOUNT, a.BILLTYPE, a.BILLSTATUS, a.PAIDAMOUNT, a.UNPAIDAMOUNT, a.THISPAY, a.SUBBRANCH, a.PAYDATE, a.CUSBILLNO, a.TOADDRESS, a.NOTE, a.ISSETTLEMENTED, b.CUATOMERNAME CUATOMERNAME from a left join base_customer b on a.CUSTOMER_ID = b.CUSTOMER_ID where 1=1 and a.PK_SOBOOKS = #{pd.PK_SOBOOKS} and IFNULL(a.dr,0)=0
and ( a.NOTE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)
and a.LDATE >= #{pd.lastStart}
and a.LDATE <= #{pd.lastEnd}
and a.CUSTOMER_ID= #{pd.CUSTOMER_ID}
and a.ISSETTLEMENTED = #{pd.ISSETTLEMENTED}
and a.BILLSTATUS = #{pd.BILLSTATUS}
order by a.LASTTIME DESC

select a.SALEBILL_ID, a.BILLCODE, b.CUATOMERNAME CUATOMERNAME, a.ALLAMOUNT, a.UNPAIDAMOUNT, a.PAIDAMOUNT, a.THISPAY, a.ISSETTLEMENTED, a.USER_ID, a.NOTE from a left join base_customer b on a.CUSTOMER_ID = b.CUSTOMER_ID where 1=1 and a.PK_SOBOOKS = #{pd.PK_SOBOOKS} and IFNULL(a.dr,0)=0
and ( NOTE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)
and a.CUSTOMER_ID= #{pd.CUSTOMER_ID}
and a.ISSETTLEMENTED != 1
and a.ISSETTLEMENTED = #{pd.ISSETTLEMENTED}
and a.BILLSTATUS = 2 order by a.LASTTIME DESC

