mapper.select:
select 

from 

where 
	STOCKCHECK_ID = #{STOCKCHECK_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(DR,0)=0

mapper.update:
update

set DR = 1
where 
	STOCKCHECK_ID = #{STOCKCHECK_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}

