set SETTEDNUMANDID = #{SETTEDNUMANDID}
where 
	STOCKCHECKBODY_ID = #{STOCKCHECKBODY_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0

update

set 
	GOODCODE_ID = #{newGOODCODE}
where 
GOODCODE_ID = #{GOODCODE}
and PK_SOBOOKS = #{PK_SOBOOKS}

