mapper.update:
update

set DR = 1
where 
	STOCKCHECK_ID = #{STOCKCHECK_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}

update

set 
	WAREHOUSE_ID = #{WAREHOUSE_ID},
	LOTNUM = #{LOTNUM},
	NOTE = #{NOTE}
where 
	STOCKCHECK_ID = #{STOCKCHECK_ID}
	and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0

update

set 
where 
    STOCKCHECK_ID = #{STOCKCHECK_ID}
and PK_SOBOOKS = #{PK_SOBOOKS}
and IFNULL(dr,0)=0

mapper.select:
select 
	
from
 
where 
	 STOCKCHECK_ID = #{STOCKCHECK_ID}
    and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0

select 
	
from
 
where 
	 CUSTOMERSETBILL_ID = #{CUSTOMERSETBILL_ID}
    and PK_SOBOOKS = #{PK_SOBOOKS}
	and IFNULL(dr,0)=0

select a.PK_SOBOOKS, a.STOCKCHECK_ID, a.LDATE, a.BILLCODE, a.USER_ID, a.WAREHOUSE_ID, a.LOTNUM, a.NOTE, b.WHNAME WAREHOUSE_NAME from a left join base_warehouse b on a.WAREHOUSE_ID = b.WAREHOUSE_ID where 1=1 and a.PK_SOBOOKS = #{pd.PK_SOBOOKS} and IFNULL(a.dr,0)=0
and ( a.NOTE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)
and a.LDATE >= #{pd.lastStart}
and a.LDATE <= #{pd.lastEnd}
order by a.LASTTIME DESC

